package Week1.day1;

public class NegativeToPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=-40;
		if (num<0) {
			
			num= -num;
			System.out.println("The number -40 is converted to " +num);
			
		} 
			
		else {
			System.out.println(num);
		}
	}

}
